class profile extends Component {
	constructor(props){
        super(props);
        this.state = {
			questionsFetched:false,
			answersFetched:false,
			blogsFetched:false,
			blogDemandsFetched:false,
			questions_user:[],
			answers_user:[],
			blogs_user:[],
			blogDemands_user:[],
			skipQues:0,
			limitQues:20,
			skipAns:0,
			limitAns:20,
			skipBlog:0,
			limitBlog:20,
			skipBlogDemand:0,
